/* config.h - Configuration for SpeexDSP on Windows
 * This file is automatically generated for Windows builds.
 */

#ifndef CONFIG_H
#define CONFIG_H

/* Enable floating point support */
#define FLOATING_POINT

/* Use the KISS FFT library for FFT */
#define USE_KISS_FFT

/* Use the small FFT library */
#define USE_SMALLFT

/* Define to empty if `const' does not conform to ANSI C. */
/* #undef const */

/* Define to `__inline__' or `__inline' if that's what the C compiler
   calls it, or to nothing if 'inline' is not supported under any name.  */
#ifndef __cplusplus
#define inline __inline
#endif

/* Define to `unsigned int' if <sys/types.h> does not define. */
/* #undef size_t */

/* Version number of package */
#define VERSION "1.2.1"

/* Enable SSE support */
#ifdef _M_X64
#define _USE_SSE
#define _USE_SSE2
#endif

/* The size of `int', as computed by sizeof. */
#define SIZEOF_INT 4

/* The size of `long', as computed by sizeof. */
#ifdef _WIN64
#define SIZEOF_LONG 4
#else
#define SIZEOF_LONG 4
#endif

/* The size of `short', as computed by sizeof. */
#define SIZEOF_SHORT 2

/* Enable VAR_ARRAYS if supported (not on MSVC) */
/* #undef VAR_ARRAYS */

/* Export symbols from DLL */
#define EXPORT

/* Disable restrict keyword for MSVC */
#ifdef _MSC_VER
#define restrict
#define __restrict__
#endif

#endif /* CONFIG_H */
